伤害加值: {{baseDamage + addonDamage}}

体格: {{baseBuild + addonBuild}}


            <Var name="basemove"  value="AND(敏捷<体型,力量<体型) ? 7 : AND(敏捷>体型,力量>体型) ? 9 : 8" />


移动速度: {{basemove - submove}}

每轮可移动: {{(basemove - submove) * 5}} 码(米)

FieldSet.Script:
function getSkillNameDetail(skillName) {
  if(_indexOf(skillName, '(') >= 0) {
    /* 有指定专精 */
    var tmp = skillName.split('(');
    var mastery = tmp[1].replace(')', '').split(',');
    return {
      name: tmp[0],
      mastery: mastery
    }
  }else {
    /* 没有指定专精 */
    return {
      name: skillName,
      mastery: []
    }
  }
}
function getSkillInfo(skillName) {
  var name = getSkillNameDetail(skillName).name;
  return _find(global.skill, ['name', name]);
}
function getSkillSubNames(skillName) {
  var mastery = getSkillNameDetail(skillName).mastery;
  if(mastery.length > 0) {
    /* ---------用于调试下 */
    var allNames = _map(global.getSkillInfo(skillName).sub, 'name');
    for(var item of mastery) {
      if(!allNames.includes(item)) {
        debug('指定专精不存在');
        return [];
      }
    }
    /* ---------用于调试上 */
    return mastery;
  }else {
    return _map(global.getSkillInfo(skillName).sub, 'name');
  }
}
function getSkillBase(skillName, mastery) {
  /* 优先获取专精再获取基础 */
  var skillInfo = global.getSkillInfo(skillName);
  var masteryBase = _get(_find(skillInfo.sub, ['name', mastery]), 'base');
  return masteryBase || _get(skillInfo, 'base', 0);
}

setGlobal('getSkillNameDetail', getSkillNameDetail);
setGlobal('getSkillInfo', getSkillInfo);
setGlobal('getSkillSubNames', getSkillSubNames);
setGlobal('getSkillBase', getSkillBase);

function getJobDuty() {
  const dutys = _get(global, 'selectJobInfo.duty');
  return (dutys || [])
    .map((duty) => ({name: 'skill-' + getSkillNameDetail(duty).name, skillName: duty}));
}
setGlobal('getJobDuty', getJobDuty);

function getAllSkill(allData) {
  return _keys(allData)
    .filter(x => x.startsWith('skill-'))
    .map((key) => allData[key])
}
setGlobal('getAllSkill', getAllSkill);

function getWeaponList() {
  return global.weapon.map((group) => {
    var type = group.type;
    var items = group.items;
    var names = _map(items, '0');

    return {
      name: type,
      items: names
    }
  })
}

function getWeaponInfo(weaponName) {
  return _find(_flatten(_map(global.weapon, 'items')), [0, weaponName])
}

setGlobal('getWeaponList', getWeaponList);
setGlobal('getWeaponInfo', getWeaponInfo);
